{
    sasTokenParameters = new
    {
        storageResourceUri = "<storageResourceUri>",
        token = "<token>",
        useManagedIdentity = true,
    },
    folder = "<folder>",
});
Operation operation = client.SelectiveKeyRestoreOperation(WaitUntil.Completed, "<keyName>", content);
